  `url` varchar(200) DEFAULT NULL COMMENT '外链地址',
  `hdld` text COMMENT '活动亮点',
  `Ism` int(2) NOT NULL DEFAULT '0' COMMENT '是否m站',
  `Isleft` tinyint(4) NOT NULL DEFAULT '0' COMMENT '是否显示左侧报名框',
  `address` varchar(200) NOT NULL DEFAULT '' COMMENT '活动地址',
  `istopbaoming` tinyint(2) unsigned NOT NULL DEFAULT '0' COMMENT '是否关闭顶部右侧报名框，0显示，1关闭',
  `isbottom` tinyint(2) unsigned NOT NULL DEFAULT '0' COMMENT '是否关闭新底部，0显示，1关闭',
  `zf` int(11) NOT NULL DEFAULT '0' COMMENT '转发次数',
  `qiegenum` int(11) NOT NULL COMMENT '切割的数量',
  PRIMARY KEY (`ID`),
  KEY `index` (`CityID`,`CityName`,`Title`,`UpdateTime`)
) ENGINE=MyISAM AUTO_INCREMENT=5004 DEFAULT CHARSET=utf8;

